events = Event.create([{title: "DTLA Art Walk", date: 'ISODate("2014-11-13T00:00:00Z")', time: 'ISODate("2014-11-05T18:30:00Z")', url: "http://www.downtownartwalk.org/", imageurl: "http://cbsla.files.wordpress.com/2012/11/dtla-art-walk.jpg", category: "art walk", description: "The free, self-guided, public art phenomenon known as The Downtown Art Walk  brings together art lovers and community friends to the ever evolving downtown Los Angeles. With exciting and unique offerings around every corner, downtown celebrates the arts each and every month on the 2nd Thursday.", venue: "DTLA Historic Core", address: "600 S. Spring Street, Los Angeles, CA 90014", neighborhood: "downtown la" }, {address: "1725 N Hudson Ave., Los Angeles, CA 90028", category: "free booze", date: 'ISODate("2014-11-27T00:00:00Z")', description: "You are cordially invited to join Deep Eddy Vodka for a hosted bar happy hour featuring our favorite handcrafted cocktails. RSVP early!", imageurl: "https://c2.staticflickr.com/8/7120/7413168906_4a26b891c7_z.jpg", neighborhood: "hollywood", time: 'ISODate("2014-11-05T21:00:00Z")', title: "Deep Eddy Happy Hour", url: "https://deep-eddy-event-la.eventbrite.com/", venue: "Dirty Laundry"}, {title: "Venice First Fridays", date: 'ISODate("2014-12-05T00:00:00Z")', time: 'ISODate("2014-11-05T18:00:00Z")', url: "http://www.yelp.com/biz/first-fridays-los-angeles-2", imageurl: "http://1.bp.blogspot.com/-A-IQt-v8Fic/TzrHuL0ATEI/AAAAAAAAGjw/E95i-7DfgUc/s1600/Venice%2BFirst.jpg", category: "art walk", description: "There’s no better way to check out the art (and the shopping) on Abbot Kinney Boulevard, located in the heart of Venice, than stopping by on the first Friday of every month. The neighborhood merchants come together to feature the best artists, gifts, products and music. And, of course, beverages are available.", venue: "Abbot Kinney Blvd.", address: "Abbot Kinney Blvd, Los Angeles, CA 90291", neighborhood: "venice" }, {title: "SantaCon 2014", date: 'ISODate("2014-12-13T00:00:00Z")', time: 'ISODate("2014-11-05T18:30:00Z")', url: "http://www.heathervescent.com/santacon/", imageurl: "http://nycsantacon.com/wp-content/uploads/2013/11/santa_ray.jpg", category: "community", description: "Described by its organizers as “a nonsensical Santa Claus convention that happens once a year for absolutely no reason,” SantaCon is an annual bar crawl in which participants dress as Santa Claus or other Christmas characters. SantaCon is also known as Santarchy, Santa Rampage, the Red Menace, and Santapalooza. ", venue: "All Over LA", address: "3425 E 1st St, Los Angeles, CA 90063", neighborhood: "other..." }, {title: "Westside Thanksgiving", date: 'ISODate("2014-11-27T00:00:00Z")', time: 'ISODate("2014-11-05T11:00:00Z")', url: "http://www.westsidethanksgiving.org/", imageurl: "http://worldwest.media.clients.ellingtoncms.com/img/photos/2012/11/22/112312_thanksgiving1_t670.jpg", category: "free food", description: "Join in for an annual LA tradition of serving the community with a wonderful free sit-down Thanksgiving dinner. Westside Thanksgiving also provides haircuts, blankets, clothing, hygiene kits and a children's carnival. Come by to volunteer, donate food and join in the festivities.", venue: "Greater LA VA Campus", address: "11301 Wilshire Blvd, Los Angeles, CA 90073", neighborhood: "westwood" }, {title: "New Year's Eve LA", date: 'ISODate("2014-12-31T00:00:00Z")', time: 'ISODate("2014-11-05T20:00:00Z")', url: "http://grandparkla.org/ai1ec_event/new-years-eve-la/", imageurl: "http://gloriamolina.org/wp-content/uploads/2013/12/nyela.jpg", category: "free party", description: "Grand Park’s free N.Y.E. LA celebration will include music and dancing, along with a new, spectacular interactive countdown, featuring 3-D digital video mapping on two sides of City Hall that will captivate the imagination as it salutes the “heart” of Los Angeles.", venue: "Grand Park", address: "200 N Grand Ave, Los Angeles, CA 90012", neighborhood: "downtown la" }, {title: "Monday Night Residency", date: 'ISODate("2014-11-17T00:00:00Z")', time: 'ISODate("2014-11-05T20:30:00Z")', url: "http://www.theecho.com/event/701799-monday-night-residency-los-angeles/", imageurl: "http://www.buzzbands.la/wp-content/uploads/2014/05/wartimerecitals-e1400087786905.jpg", category: "free music", description: "Get a full dose of free music at the Echo as party of Monday Night Residencies. Rock out with Wartime Recitals, Forebear, Decorator and more. RSVP now for priority entry.", venue: "Echoplex", address: "1822 Sunset Blvd, Los Angeles, CA 90026", neighborhood: "echo park" }, {address: "1717 Silver Lake Blvd, Los Angeles, CA 90026", category: "free music", date: 'ISODate("2014-11-22T00:00:00Z")', description: "\"Dance Yourself Clean\" is a no frills weekly dance party that started in Seattle and has now expanded to Los Angeles. The idea behind DYC is to give music fans a place to dance and have fun that isn’t a typical bottle service club or underground warehouse rave. Free entry before 10:30pm.", imageurl: "http://static.parastorage.com/media/a17f17_5d3ab72d62c54ce8962449b1192e47ab.jpg", neighborhood: "silver lake", time: 'ISODate("2014-11-05T21:30:00Z")', title: "Dance Yourself Clean", url: "http://www.thesatellitela.com/event/658555-dance-yourself-clean-los-angeles/", venue: "The Satellite" }, {title: "Lunchtime Yoga reTREAT", date: 'ISODate("2014-11-19T00:00:00Z")', time: 'ISODate("2014-11-05T12:15:00Z")', url: "http://grandparkla.org/ai1ec_event/lunchtime-yoga-retreat/?instance_id=45725", imageurl: "http://total-yoga.org/wp-content/uploads/2013/06/Yoga-in-the-Park.jpg", category: "free yoga", description: "Free yoga sessions designed to relax, rejuvenate and reenergize. Pop in and out, dress comfortably and be ready to retreat into Grand Park bliss. Be sure to bring your own yoga mat!", venue: "Grand Park", address: "200 North Grand Avenue, Los Angeles, CA 90012", neighborhood: "downtown la" }, {title: "International Surf Festival", date: 'ISODate("2015-07-30T00:00:00Z")', time: 'ISODate("2014-11-05T12:00:00Z")', url: "http://www.surffestival.org/", imageurl: "http://www.manhattanbeachre.com/images/surf-festival.jpg", category: "festival", description: "Hermosa Beach hosts the 54th annual International Surf Festival for a full weekend of 6-man volleyball, a surfing contest, bodysurfing contest, beach run, paddleboard race, dory race, and tons of live music + debauchery in downtown Hermosa.", venue: "Hermosa Beach", address: "Pier Ave, Hermosa Beach, CA 90254", neighborhood: "hermosa beach" }])